function dropElements(arr, func) {
  // Drop them elements.
  // console.log(arr);
  let lengthOfArray = arr.length;
  // let newArr = arr.slice();
  // Had arr.length in the for loop, but it did not pass the test
  // Move to a variable, passed test
  // Tried newArr as a copy, but that failed
  for (var i = 0; i < lengthOfArray; i++){
    if (!func(arr[0])) {
      arr.shift()
    }
  }
  // console.log(arr)
  return arr;
}

