function orbitalPeriod(arr) {
  var GM = 398600.4418;
  var earthRadius = 6367.4447;
  var piTimesTwo = (2 * Math.PI);
  var newArr = [];

  var orbitalPeriods = function(input) {
    // var avgAlt = arr[0].avgAlt; //This will fail when the avgAlt is deleted on the first pass. Need to find a better way to get value
    var avgAlt = input.avgAlt;
    // console.log(avgAlt);
    // let addedTogether = earthRadius + avgAlt;
    // let poweredUp = Math.pow(earthRadius + avgAlt, 3)
    var orbitalcalc = Math.round(piTimesTwo * (Math.sqrt((Math.pow(earthRadius + avgAlt, 3)/GM))));
    // console.log("Math: " + orbitalcalc)
    delete input.avgAlt;
    input.orbitalPeriod = orbitalcalc;
    return input;
  }

  for (var i = 0; i < arr.length; i++) {
    newArr.push(orbitalPeriods(arr[i]));
  }
  return arr;
}

