function translatePigLatin(str) {
  let vowels = /[aeiou]/gi;
  // let con = /[^aeiou]+/gi;
  let originalStr = [];
  let newStr = [];
  let finalString = "";

  if (str[0].match(vowels)) {
    return str + "way";
  } else if (str.match(vowels) === null){
    finalString = str + 'ay';
    return finalString;
  } else {
    originalStr = str.split("");
    // console.log("Original string: " + originalStr);
    for (var i = 0; i < str.length; i++) {
      if (!str[i].match(vowels)){
        newStr.push(str[i]);
        // originalStr.splice(0, 1);
        // console.log("Here: " + originalStr);
      } else {
        i = str.length;
      }
    }
    let toRemove = newStr.length;
    originalStr.splice(0, toRemove);
    // console.log("After splice: " + originalStr);
    // console.log("New string: " + newStr);
    // console.log("Original with removed: " + originalStr);
  }
  let newNew = originalStr.concat([...newStr]);
  // console.log("Here: " + newNew);
  // console.log("The final: " + originalStr + newStr.join('') + "ay");
  // console.log("Another: " + newNew.join("") + "ay")
  return newNew.join("") + "ay";
  // return str;
}

translatePigLatin("qwqwe");
translatePigLatin("consonant");
translatePigLatin("eight");
translatePigLatin("glove");

