function myReplace(str, before, after) {
  let beforeWord = arguments[1];
  let afterWord = arguments[2];
  let re = /[A-Z]/;

  // Need to find the case of the first character of the before word
  beforeWord = before.split("");
  afterWord = after.split("");

  if (beforeWord[0].match(re)) {
    let newLetter = afterWord[0].toUpperCase();
    afterWord.splice(0, 1, newLetter);
  }

  let updatedWord = afterWord.join("");
  // console.log("Last: " + updatedWord);

  let arr = str.split(" ");
  // console.log(arr);
  let newArr = [];

  // Need to update this after getting the case part right
  for (var i = 0; i < arr.length; i++) {
    if (arr[i] != before) {
      newArr.push(arr[i])
    } else {
      arr[i] = updatedWord;
      newArr.push(arr[i]);
    }
  }
  arr = arr.join(" ");
  // console.log(arr)

  return arr;
}

myReplace("A quick brown fox jumped over the lazy dog", "jumped", "leaped");
myReplace("Let us get back to more Coding", "Coding", "algorithms");
