function sumPrimes(num) {
  // Call the getPrimes function
  let thePrimes = getPrimes(num);
  const reducer = (accumulator, currentValue) => accumulator + currentValue;
  // Use .reduce to sum the prime numbers
  let sumNum = thePrimes.reduce(reducer);
  return sumNum;
}
  function getPrimes(max) {
    // From "https://stackoverflow.com/questions/11966520/how-to-find-prime-numbers-between-0-100"
    let sieve = []
    let primes = [];
    for (var i = 2; i <= max; ++i) {
      if (!sieve[i]) {
      // i has not been marked -- it is prime
        primes.push(i);
        for (var j = i << 1; j <= max; j += i) {
          sieve[j] = true;
        }
      }
    }
    return primes;
}

sumPrimes(10);
